/**
 * Weather API Service
 * Handles all interactions with the OpenWeatherMap API
 */

import { WEATHER_API, APP_CONFIG, ERROR_MESSAGES, WEATHER_CONDITIONS, FEATURES } from '../constants/config.js';
import { getCached, buildURL, APIError } from '../utils/api.js';

/**
 * Weather data transformation and normalization
 */
class WeatherDataTransformer {
  /**
   * Transform current weather API response to normalized format
   * @param {Object} apiResponse - Raw API response
   * @returns {Object} - Normalized weather data
   */
  static transformCurrentWeather(apiResponse) {
    if (!apiResponse || !apiResponse.main || !apiResponse.weather) {
      throw new APIError(ERROR_MESSAGES.DATA.INVALID_RESPONSE);
    }

    const weather = apiResponse.weather[0] || {};
    const main = apiResponse.main || {};
    const wind = apiResponse.wind || {};
    const sys = apiResponse.sys || {};

    return {
      location: {
        name: apiResponse.name || 'Unknown',
        country: sys.country || '',
        coordinates: {
          lat: apiResponse.coord?.lat || 0,
          lon: apiResponse.coord?.lon || 0
        },
        timezone: apiResponse.timezone || 0,
        sunrise: sys.sunrise ? new Date(sys.sunrise * 1000) : null,
        sunset: sys.sunset ? new Date(sys.sunset * 1000) : null
      },
      current: {
        temperature: Math.round(main.temp || 0),
        feelsLike: Math.round(main.feels_like || 0),
        humidity: main.humidity || 0,
        pressure: main.pressure || 0,
        windSpeed: Math.round((wind.speed || 0) * 3.6), // Convert m/s to km/h
        windDirection: wind.deg || 0,
        windGust: wind.gust ? Math.round(wind.gust * 3.6) : null,
        visibility: apiResponse.visibility ? Math.round(apiResponse.visibility / 1000) : null,
        uvIndex: null, // Not available in current weather endpoint
        cloudCover: apiResponse.clouds?.all || 0,
        condition: {
          id: weather.id || 800,
          main: weather.main || 'Clear',
          description: weather.description || 'Clear sky',
          icon: weather.icon || '01d'
        },
        timestamp: new Date(apiResponse.dt * 1000)
      },
      units: {
        temperature: 'celsius',
        windSpeed: 'kmh',
        pressure: 'hPa',
        visibility: 'km'
      }
    };
  }

  /**
   * Transform forecast API response to normalized format
   * @param {Object} apiResponse - Raw API response
   * @returns {Object} - Normalized forecast data
   */
  static transformForecast(apiResponse) {
    if (!apiResponse || !apiResponse.list || !Array.isArray(apiResponse.list)) {
      throw new APIError(ERROR_MESSAGES.DATA.INVALID_RESPONSE);
    }

