    this.elements.input.setAttribute('aria-expanded', 'true');
    this.elements.resultsContainer.setAttribute('aria-live', 'polite');
  }

  /**
   * Create result item element
   * @param {Location} location - Location data
   * @param {number} index - Item index
   * @returns {HTMLElement} - Result item element
   */
  createResultItem(location, index) {
    const item = document.createElement('div');
    item.className = 'search-result-item';
    item.setAttribute('role', 'option');
    item.setAttribute('aria-selected', 'false');
    item.setAttribute('data-index', index);

    // Main location name
    const name = document.createElement('div');
    name.className = 'result-name';
    name.textContent = location.name;
    item.appendChild(name);

    // Location details (state, country)
    const details = [];
    if (location.state && location.state !== location.name) {
      details.push(location.state);
    }
    if (location.country) {
      details.push(location.country);
    }

    if (details.length > 0) {
      const detailsElement = document.createElement('div');
      detailsElement.className = 'result-details';
      detailsElement.textContent = details.join(', ');
      item.appendChild(detailsElement);
    }

    // Add coordinates for debugging
    if (FEATURES.DEBUG_MODE) {
      const coords = document.createElement('div');
      coords.className = 'result-coords';
      coords.textContent = `${location.lat.toFixed(2)}, ${location.lon.toFixed(2)}`;
      item.appendChild(coords);
    }

    // Click handler
    item.addEventListener('click', (event) => {
      event.preventDefault();
      this.handleResultClick(location);
    });

    // Hover handler
    item.addEventListener('mouseenter', () => {
      this.setSelectedIndex(index);
    });

    return item;
  }

  /**
   * Select next result item
   */
  selectNext() {
    if (this.searchResults.length === 0) return;

    const nextIndex = (this.selectedIndex + 1) % this.searchResults.length;
    this.setSelectedIndex(nextIndex);
  }

  /**
   * Select previous result item
   */
  selectPrevious() {
    if (this.searchResults.length === 0) return;

    const prevIndex = this.selectedIndex <= 0 
      ? this.searchResults.length - 1 
      : this.selectedIndex - 1;
    this.setSelectedIndex(prevIndex);
  }

